    //         setBlogs(blogs)
    //     })
    // }, [])

    console.log(blogs)

    const blogsList = blogs.map((blog) => {
        return <BlogItem key={blog.id} 
        title={blog.title} 
        content={blog.content} 
        createdAt={blog.created_at} 
        user={blog.user_id} />
    }) 





    return (
        <>
            <h1>Blogs</h1>
            {blogsList}

        </>
    )
}

export default BlogContainer;
